contracts/facets/RepayFacet.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.17;

import "@openzeppelin/contracts/token/ERC20/IERC20.sol";
import "@openzeppelin/contracts/security/ReentrancyGuard.sol";
import "../libraries/LibDiamond.sol";

import "hardhat/console.sol";
import {AppStorage, Token, LibAppStorage, BorrowedToken} from "../libraries/LibAppStorage.sol";
import {LibDiamond} from "../libraries/LibDiamond.sol";

contract RepayFacet is ReentrancyGuard {
    AppStorage internal s;
    error UnsupportedToken();
    error NoAmountAvailableToBorrow();
    error CannotBorrowAmount();
    error ShouldBeGreaterThanZero();
    error InsufficientFunds();
    error TokenNotBorrowed();

    function repay(address tokenAddress, uint256 tokenAmount) external {
        // Token amount has to be greater than zero
        if (tokenAmount <= 0) {
            revert ShouldBeGreaterThanZero();
        }

        // token has to be supported
        int index = LibAppStorage._indexOf(tokenAddress, s.supportedTokens);

        if (index == -1) {
            revert UnsupportedToken();
        }

        // Check if the user has already borrowed the token below.
        BorrowedToken[] memory userTokensBorrowed = s.tokensBorrowed[
            msg.sender
        ];

        // User has once supplied
        int tokenIndex = indexOf(tokenAddress, userTokensBorrowed);

        if (tokenIndex == -1) {
            revert TokenNotBorrowed();
        } else {
            // Update the value of the token in the contract.

            BorrowedToken memory borrowedToken = s.tokensBorrowed[msg.sender][
                uint256(tokenIndex)
            ];

            uint256 noOfDays = (block.timestamp -
                borrowedToken.startAccumulatingDay) / 86400;

            uint256 totalInterest = (borrowedToken.stableRate *
                borrowedToken.amountBorrowed) / 10000;

            uint256 accumulatedInterest = (noOfDays * totalInterest) / 365;

            uint256 totalToRepay = tokenAmount + accumulatedInterest;

            if (IERC20(tokenAddress).balanceOf(address(this)) < totalToRepay) {
                revert InsufficientFunds();
            }

            s
            .tokensBorrowed[msg.sender][uint(tokenIndex)]
                .amountBorrowed -= tokenAmount;

            s
            .tokensBorrowed[msg.sender][uint(tokenIndex)]
                .startAccumulatingDay = block.timestamp;

            require(
                IERC20(tokenAddress).transferFrom(
                    msg.sender,
                    address(this),
                    totalToRepay
                ),
                "Insufficient funds"
            );
        }
    }

    function getTokenMaxAvailableToRepay(
        address user,
        address tokenAddress
    ) public view returns (uint256 totalToRepay) {
        // Check if the user has already borrowed the token below.
        BorrowedToken[] memory userTokensBorrowed = s.tokensBorrowed[user];

        // User has once supplied
        int tokenIndex = indexOf(tokenAddress, userTokensBorrowed);

        if (tokenIndex == -1) {
            totalToRepay = 0;
        } else {
            BorrowedToken memory borrowedToken = s.tokensBorrowed[user][
                uint256(tokenIndex)
            ];

            uint256 noOfDays = (block.timestamp -
                borrowedToken.startAccumulatingDay) / 86400;

            uint256 totalInterest = (borrowedToken.stableRate *
                borrowedToken.amountBorrowed) / 10000;

            uint256 accumulatedInterest = (noOfDays * totalInterest) / 365;

            totalToRepay = borrowedToken.amountBorrowed + accumulatedInterest;
        }
    }

    function indexOf(
        address tokenAddress,
        BorrowedToken[] memory tokenArray
    ) public pure returns (int256) {
        for (uint i = 0; i < tokenArray.length; i++) {
            BorrowedToken memory currentBorrowedToken = tokenArray[i];
            if (currentBorrowedToken.tokenAddress == tokenAddress) {
                return int256(i);
            }
        }

        return -1;
    }
}

contracts/facets/SupplyFacet.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.17;

import "../interfaces/IERC20.sol";
import "@openzeppelin/contracts/security/ReentrancyGuard.sol";
import "../libraries/LibDiamond.sol";

import "hardhat/console.sol";
import {AppStorage, SuppliedToken, LibAppStorage, Token, Modifiers} from "../libraries/LibAppStorage.sol";

contract SupplyFacet is ReentrancyGuard, Modifiers {
    // DiamondStorage internal d;

    error InsufficientFunds();
    error InvalidAmount();
    error TokenNotSupported();
    error TokenNotFound();
    error AlreadyOn();
    error AlreadyOff();
    error NotSupplier();
    error CannotSwitchOffCollateral();

    function supplyToken(address tokenAddress, uint256 tokenAmount) external {
        IERC20 tokenContract = IERC20(tokenAddress);
        uint8 decimals = tokenContract.decimals();

        // You can only supply a token that is supported
        int index = LibAppStorage._indexOf(tokenAddress, s.supportedTokens);

        if (index == -1) {
            revert TokenNotSupported();
        }

        // You should have more than enough to supply
        if (tokenContract.balanceOf(msg.sender) < tokenAmount) {
            revert InsufficientFunds();
        }

        // You cannot supply 0 token
        if (tokenAmount <= 0) {
            revert InvalidAmount();
        }

        SuppliedToken[] memory userTokenSupplied = s.tokensSupplied[msg.sender];
        Token memory tokenDetails = s.addressToToken[tokenAddress];

        if (userTokenSupplied.length == 0) {
            // User has never called this function before.
            SuppliedToken memory suppliedToken;
            suppliedToken.tokenAddress = tokenAddress;
            suppliedToken.supplierAddress = msg.sender;
            suppliedToken.amountSupplied = tokenAmount;
            suppliedToken.supplyInterest = 0;
            suppliedToken.supplyStableRate = tokenDetails.supplyStableRate;
            suppliedToken.startAccumulatingDay = 0;
            suppliedToken.isCollateral = true;

            s.tokensSupplied[msg.sender].push(suppliedToken);
            s.allSuppliers.push(msg.sender);
        } else {
            // User has once supplied
            int tokenIndex = indexOf(tokenAddress, userTokenSupplied);

            if (tokenIndex == -1) {
                // Token has never been supplied before
                SuppliedToken memory suppliedToken;
                suppliedToken.tokenAddress = tokenAddress;
                suppliedToken.supplierAddress = msg.sender;
                suppliedToken.amountSupplied = tokenAmount;
                suppliedToken.supplyInterest = 0;
                suppliedToken.supplyStableRate = tokenDetails.supplyStableRate;
                suppliedToken.startAccumulatingDay = 0;
                suppliedToken.isCollateral = true;

                s.tokensSupplied[msg.sender].push(suppliedToken);
            } else {
                // Update the value of the token in the contract.
                SuppliedToken storage suppliedToken = s.tokensSupplied[
                    msg.sender
                ][uint(tokenIndex)];
                suppliedToken.amountSupplied += tokenAmount;
            }
        }

        // Contract takes token from your wallet
        require(
            tokenContract.transferFrom(msg.sender, address(this), tokenAmount),
            "Transfer from failed"
        );

        /////  After supplying, you will get some LAR token minted to you.
        uint256 tokenAmountInUsd = LibAppStorage._getUsdEquivalent(
            s,
            tokenAmount,
            tokenAddress
        );

        // Assuming that 1 LAR = 1 USD. Send LAR token equivalent to the tokenAmountInUsd.
        // This will be burnt whenever they withdraw from the token.
        IERC20 larToken = IERC20(s.larTokenAddress);

        // Convert USD
        uint256 larTokenToTransfer = (tokenAmountInUsd * 10 ** 18) /
            10 ** decimals;

        require(
            larToken.transfer(msg.sender, larTokenToTransfer),
            "Transfer failed"
        );
    }

    function switchOnCollateral(address tokenAddress) external {
        // msg.sender must have supplied this token already
        SuppliedToken[] memory suppliedTokens = s.tokensSupplied[msg.sender];

        if (suppliedTokens.length == 0) {
            revert TokenNotFound();
        }

        int256 tokenIndex = indexOf(tokenAddress, suppliedTokens);

        if (tokenIndex == -1) {
            revert TokenNotFound();
        }

        // If it has been switched on before, there is no need to switch it on again

        SuppliedToken storage suppliedToken = s.tokensSupplied[msg.sender][
            uint(tokenIndex)
        ];

        if (suppliedToken.supplierAddress != msg.sender) {
            revert NotSupplier();
        }
        if (suppliedToken.isCollateral == true) {
            revert AlreadyOn();
        }

        suppliedToken.isCollateral = true;
        suppliedToken.supplyInterest = 0;
        suppliedToken.startAccumulatingDay = 0;
    }

    function swithOffCollateral(address tokenAddress) external {
        // msg.sender must have supplied this token already
        SuppliedToken[] memory suppliedTokens = s.tokensSupplied[msg.sender];
        Token memory tokenDetails = s.addressToToken[tokenAddress];
        uint8 decimals = IERC20(tokenAddress).decimals();

        if (suppliedTokens.length == 0) {
            revert TokenNotFound();
        }

        int256 tokenIndex = indexOf(tokenAddress, suppliedTokens);

        if (tokenIndex == -1) {
            revert TokenNotFound();
        }

        // If it has been switched on before, there is no need to switch it on again

        SuppliedToken storage suppliedToken = s.tokensSupplied[msg.sender][
            uint(tokenIndex)
        ];

        if (suppliedToken.supplierAddress != msg.sender) {
            revert NotSupplier();
        }
        if (suppliedToken.isCollateral == false) {
            revert AlreadyOff();
        }

        // Make sure that switching it off will not affect the borrowed tokens. I still have to convert those 3 values to USD
        uint256 totalAvailableLoanAmountInUsd = (LibAppStorage._maxLTV(
            s,
            msg.sender
        ) * LibAppStorage._getUserTotalCollateralInUsd(s, msg.sender)) / 10000;
        uint256 tokenAvailableLoanAmount = getTokenAvailableLoanAmount(
            msg.sender,
            tokenAddress
        );
        uint256 tokenAvailableLoanAmountInUsd = LibAppStorage._getUsdEquivalent(
            s,
            tokenAvailableLoanAmount,
            tokenAddress
        );
        uint256 scaledTokenAvailableLoanAmountInUsd = ((tokenAvailableLoanAmountInUsd *
                10 ** 18) / 10 ** decimals);
        // uint256 userTotalBorrowed = getTotalBorrowed(msg.sender);
        uint256 borrowedAmountInUsd = LibAppStorage._getUserTotalBorrowedInUsd(
            s,
            msg.sender
        );

        // console.log("\n\n\n");

        // console.logInt(int256(totalAvailableLoanAmountInUsd));
        // console.logInt(int256(tokenAvailableLoanAmountInUsd));
        // console.logInt(int(borrowedAmountInUsd));

        // console.log("\n\n\n");

        if (
            (int256(totalAvailableLoanAmountInUsd) -
                int256(scaledTokenAvailableLoanAmountInUsd)) <
            int(borrowedAmountInUsd)
        ) {
            revert CannotSwitchOffCollateral();
        }

        suppliedToken.isCollateral = false;
        suppliedToken.supplyInterest =
            (tokenDetails.supplyStableRate * suppliedToken.amountSupplied) /
            10000;
        suppliedToken.startAccumulatingDay = block.timestamp;
    }

    function indexOf(
        address tokenAddress,
        SuppliedToken[] memory tokenArray
    ) public pure returns (int256) {
        for (uint i = 0; i < tokenArray.length; i++) {
            SuppliedToken memory currentSuppliedToken = tokenArray[i];
            if (currentSuppliedToken.tokenAddress == tokenAddress) {
                return int256(i);
            }
        }

        return -1;
    }

    function getTokenAvailableLoanAmount(
        address user,
        address tokenAddress
    ) public view returns (uint256) {
        SuppliedToken[] memory suppliedTokens = s.tokensSupplied[user];

        int256 tokenIndex = indexOf(tokenAddress, suppliedTokens);

        SuppliedToken memory suppliedToken = suppliedTokens[
            uint256(tokenIndex)
        ];
        Token memory tokenDetails = s.addressToToken[
            suppliedToken.tokenAddress
        ];

        uint256 availableAmount = (tokenDetails.loanToValue *
            suppliedToken.amountSupplied) / 10000;

        return availableAmount;
    }
}

contracts/facets/WithdrawFacet.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.17;

import "@openzeppelin/contracts/security/ReentrancyGuard.sol";
import "../libraries/LibDiamond.sol";
import "../interfaces/IERC20.sol";

import "hardhat/console.sol";
import {AppStorage, Token, LibAppStorage, SuppliedToken, Modifiers} from "../libraries/LibAppStorage.sol";
import {LibDiamond} from "../libraries/LibDiamond.sol";

contract WithdrawFacet is ReentrancyGuard, Modifiers {
    error UnsupportedToken();
    error NoAmountAvailableToWithdraw();
    error CannotWithdrawAmount();
    error ShouldBeGreaterThanZero();
    error InsufficientFunds();
    error TokenNotSupplied();

    error OnlyOwnerCanCall();

    function withdraw(address tokenAddress, uint256 tokenAmount) external nonReentrant {
         // Token amount has to be greater than zero
        if (tokenAmount <= 0){
            revert ShouldBeGreaterThanZero();
        }

        // token has to be supported
        int index = LibAppStorage._indexOf(tokenAddress, s.supportedTokens);

        if (index == -1){
            revert UnsupportedToken();
        }

        // You must have enough collateral.
        int256 maxAvailableToWithdrawInUsd = getMaxAvailableToWithdrawInUsd(msg.sender);

        // console.log("Maximum available to withdraw: ", uint256(maxAvailableToWithdrawInUsd));

        if (maxAvailableToWithdrawInUsd <= 0){
            revert NoAmountAvailableToWithdraw();
        }

        uint256 tokenAmountInUsd = LibAppStorage._getUsdEquivalent(s, tokenAmount, tokenAddress);
        uint8 decimals = IERC20(tokenAddress).decimals();
        uint256 scaledTokenAmountInUsd = (tokenAmountInUsd * 10**18) / 10**decimals;


          if ((maxAvailableToWithdrawInUsd - int256(scaledTokenAmountInUsd)) < 0){
            revert CannotWithdrawAmount();
        }
        
        // Make sure we have more than enough of the token in the smart contract.
        if (IERC20(tokenAddress).balanceOf(address(this)) < tokenAmount){
            revert InsufficientFunds();
        }

         // Check if the user has already borrowed the token below.
        SuppliedToken[] memory userTokensSupplied = s.tokensSupplied[msg.sender];
         
            // User has once supplied
            int tokenIndex = indexOf(tokenAddress, userTokensSupplied);

            if (tokenIndex == -1) {
              revert TokenNotSupplied();

            } else {
                // Update the value of the token in the contract.
               

                SuppliedToken memory suppliedToken = s.tokensSupplied[msg.sender][uint256(tokenIndex)];
                uint totalWithdrawalAmount = tokenAmount;
                uint256 larTokenToBurn = LibAppStorage._getUsdEquivalent(s, scaledTokenAmountInUsd, tokenAddress);

             
                // If it is not used as collateral, send interest to the user. Otherwise, do not send
                if (!suppliedToken.isCollateral){
                    uint256 noOfDays = (block.timestamp - suppliedToken.startAccumulatingDay) / 86400;
                    uint256 totalInterest = (suppliedToken.supplyStableRate * suppliedToken.amountSupplied) / 10000;
                    uint256 accumulatedInterest = (noOfDays * totalInterest) / 365;

                    totalWithdrawalAmount += accumulatedInterest;

                }

                 s.tokensSupplied[
                    msg.sender
                ][uint(tokenIndex)].amountSupplied -= tokenAmount;

                 s.tokensSupplied[
                    msg.sender
                ][uint(tokenIndex)].startAccumulatingDay = block.timestamp;

                  require(IERC20(tokenAddress).transfer(msg.sender, totalWithdrawalAmount), "Insufficient funds");
                  require(IERC20(s.larTokenAddress).burn(msg.sender, larTokenToBurn), "Failed to burn");



            }
        
    }   

     function getMaxAvailableToWithdrawInUsd(address user) public view returns(int256){
        uint256 maxLTV = LibAppStorage._maxLTV(s, user);
        uint256 totalCollateralInUsd = LibAppStorage._getUserTotalCollateralInUsd(s, user);
        uint256 totalBorrowedInUsd = LibAppStorage._getUserTotalBorrowedInUsd(s, user);

        return int256(totalCollateralInUsd - (totalBorrowedInUsd * 10000 ) / maxLTV);
   }

    function indexOf(
        address tokenAddress,
        SuppliedToken[] memory tokenArray
    ) public pure returns (int256) {
        for (uint i = 0; i < tokenArray.length; i++) {
            SuppliedToken memory currentSuppliedToken = tokenArray[i];
            if (currentSuppliedToken.tokenAddress == tokenAddress) {
                return int256(i);
            }
        }

        return -1;
    }



}

